(defn parse-line [line]
  (let [matcher (re-matcher #"(\d+)-(\d+) (\w+): (.*)" line)
        [min max letter pwd] (drop 1 (re-find matcher))]
    {:min    (Integer/parseInt min)
     :max    (Integer/parseInt max)
     :letter letter
     :pwd    pwd}))

(defn parse-input [input]
  (let [lines (str/split-lines input)]
    (map parse-line lines)))

(defn is-valid-part1? [{min-qtd :min
                        max-qtd :max
                        letter  :letter
                        pwd     :pwd}]
  (let [pwd-letters (str/split pwd #"")
        count-letter (count (filter #(= letter %) pwd-letters))]
    (<= min-qtd count-letter max-qtd)))

(defn is-valid-part2? [{first-pos  :min
                        second-pos :max
                        letter     :letter
                        pwd        :pwd}]
  (let [pwd-letters (str/split pwd #"")
        fst-letter (nth pwd-letters (dec first-pos))
        snd-letter (nth pwd-letters (dec second-pos))]
    (not= (= fst-letter letter)
          (= snd-letter letter))))

(defn part1 [input]
  (->> (parse-input input)
       (map is-valid-part1?)
       (filter true?)
       count))

(defn part2 [input]
  (->> (parse-input input)
       (map is-valid-part2?)
       (filter true?)
       count))

(comment
  (part1 input)
  (part2 input)
  )
